          </View>
          <Right />
        </Header>
        <View>
          {this.state.url !== "" ? (
            <View style={{ alignSelf: "center", marginTop: 6 }}>
              <Image
                source={{
                  uri:
                    //   "http://ec2-13-234-48-248.ap-south-1.compute.amazonaws.com/wf/" +
                    this.state.url,
