                  style={{
                    fontFamily: "montserrat_arabic_regular",
                    fontSize: wp(3)
                  }}
                >
                  {lan == "en" ? "Date:" : ":تاريخ"}
                </Text>
                <Text style={styles.timeTextStyle}>20-03-2012</Text>
              </View>
