                  <View
                    style={{
                      backgroundColor: "#f9f9f9",
                      paddingHorizontal: wp(3),
                      borderRadius: wp(2),
                      paddingVertical: hp(1),
                      borderWidth: 1,
                      borderColor: "#ccc"
                    }}
                  >
                    <Image
                      source={require("../assets/Booking-Request-min.png")}
                      style={{
                        width: wp(12),
                        resizeMode: "contain",
                        height: hp(9),
                        tintColor:
                          navigation.state.params.item.statusid == 1 &&
                          theme.brownColor
