                    />
                  </View>
                  <View>
                    <View
                      style={{
                        width: wp(15),
                        borderWidth: 1,
                        borderColor:
                          navigation.state.params.item.statusid == 1 &&
                          theme.brownColor
                      }}
                    ></View>
                  </View>
                </View>
                <View style={styles.textcontainerStyle}>
                  <Text style={{ alignSelf: "flex-start", fontSize: wp(4) }}>
                    {lan == "en" ? "Booking Request" : "طلب الحجز"}
                  </Text>
                </View>
              </View>

              {/* --------------- */}
              <View>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center"
                  }}
                >
                  <View
                    style={{
                      backgroundColor: "#f9f9f9",
                      paddingHorizontal: wp(3),
                      borderRadius: wp(2),
                      paddingVertical: hp(1),
                      borderWidth: 1,
                      borderColor: "#ccc"
                    }}
                  >
                    <Image
                      source={require("../assets/Booking-Accept-min.png")}
                      style={{
                        width: wp(12),
                        resizeMode: "contain",
                        height: hp(9),
                        tintColor:
                          navigation.state.params.item.statusid == 2 &&
                          theme.brownColor
                      }}
                    />
                  </View>
                  <View>
                    <View
                      style={{
                        width: wp(15),
                        borderWidth: 1,
                        borderColor:
                          navigation.state.params.item.statusid == 2 &&
                          theme.brownColor
                      }}
                    ></View>
                  </View>
                </View>
                <View style={styles.textcontainerStyle}>
                  <Text style={{ alignSelf: "flex-start", fontSize: wp(4) }}>
                    {lan == "en" ? "Booking Accepted" : "تم قبول الحجز"}
                  </Text>
                </View>
              </View>
              <View>
                <View
                  style={{
                    flexDirection: "row",
                    alignItems: "center"
                  }}
                >
                  <View
                    style={{
                      backgroundColor: "#f9f9f9",
                      paddingHorizontal: wp(3),
                      borderRadius: wp(2),
                      paddingVertical: hp(1),
                      borderWidth: 1,
                      borderColor: "#ccc"
                    }}
                  >
                    <Image
                      source={require("../assets/Booking-Complete-min.png")}
                      style={{
                        width: wp(12),
                        resizeMode: "contain",
                        height: hp(9)
                      }}
                    />
                  </View>
                  <View></View>
                </View>
                <View style={styles.textcontainerStyle}>
                  <Text style={{ alignSelf: "flex-start", fontSize: wp(4) }}>
                    {lan == "en" ? "Booking Completed" : "اكتمل الحجز"}
                  </Text>
                </View>
              </View>
            </View>
          )}
        </ScrollView>
      </View>
      {/* ---------------footer------------- */}
      <View style={styles.footerStyle}>
        <TouchableOpacity style={styles.footerItemContainerStyle}>
          <Image
            style={{
              height: hp(4),
              width: wp(6),
              resizeMode: "contain",
              tintColor: "#ccc"
            }}
            source={require("../assets/Salon-min.png")}
          />
          <Text
            style={[
              styles.tabtextStyle,
              { color: Salon ? theme.brownColor : "#ccc" }
            ]}
          >
            {lan == "en" ? "Salons" : "صالونات"}
          </Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.footerItemContainerStyle}>
          <Ionicons
            name="md-cart"
            size={30}
            color={cart ? theme.brownColor : "#ccc"}
          />
          <Text
            style={[
              styles.tabtextStyle,
              { color: cart ? theme.brownColor : "#ccc" }
            ]}
          >
            {lan == "en" ? " My Bookings" : "حجوزاتي"}
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.footerItemContainerStyle}
          onPress={() => navigation.navigate("BookingProfileScreen")}
        >
          <Ionicons
            name="ios-person"
            size={30}
            color={Profile ? theme.brownColor : "#ccc"}
          />
          <Text
            style={[
              styles.tabtextStyle,
              { color: Profile ? theme.brownColor : "#ccc" }
            ]}
          >
            {lan == "en" ? "My Profile" : "ملفي"}
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};
