import * as Analytics from "expo-firebase-analytics";
import * as Permissions from "expo-permissions";

import {
  AsyncStorage,
  Dimensions,
  Image,
  Modal,
  ScrollView,
  TouchableOpacity,
  View,
  TextInput,
} from "react-native";
import {
  Container,
  Content,
  Footer,
  Header,
  Input,
  Item,
  Left,
  Right,
  Text,
  Thumbnail,
  Title,
  Toast,
  Button,
} from "native-base";

import CalendarPicker from "react-native-calendar-picker";
import { Ionicons } from "@expo/vector-icons";
import { LinearGradient } from "expo-linear-gradient";
import { NavigationEvents } from "react-navigation";
import * as Updates from "expo-updates";
import * as Notifications from "expo-notifications";
import React from "react";
import Spinner from "react-native-loading-spinner-overlay";
import { StatusBar } from "expo-status-bar";
const withPaint =
  "1- This is an estimated price for the job, the actual price will be shared by the professional depending upon the distance and complexity of the work\r\n2- The price displayed is for service only and it does not include price for any parts or materials required to perform the job\n3- The price of the paint is not included in the prices\r\n4- These prices are inclusive of the visit charge\r\n5- The professional would charge SAR 25 for the visit, in case the job is not finalized";
const withOutPaint =
  "1- This is an estimated price for the job, the actual price will be shared by the professional depending upon the distance and complexity of the work\r\n2- The price displayed is for service only and it does not include price for any parts or materials required to perform the job";
const withPaint_ar =
  "1 ) هذا هو السعر التقريبي للعمل ، سيتم تحديد السعر الحقيقي بواسطة المهني (مقدم الخدمة) على حسب المسافة وتعقيد العمل." +
  "\n" +
  "2) السعر المعروض هنا -فقط - للخدمة ولا يشمل السعر قيمة اي أجزاء أو مواد مطلوبه لأنجاز العمل." +
  "\n" +
  "3)السعر لا يشمل سعر الطلاء" +
  "\n" +
  "4) الأسعار تشمل تكاليف الزيارة" +
  "\n" +
  "5)سيكون عليك دفع 25 ريال بدل زيارة للفني في حالة لم يتم التوافق على الخدمة";
const withOutPaint_ar =
  "1 ) هذا هو السعر التقريبي للعمل ، سيتم تحديد السعر الحقيقي بواسطة المهني (مقدم الخدمة) على حسب المسافة وتعقيد العمل." +
  "\n" +
  "2) السعر المعروض هنا -فقط - للخدمة ولا يشمل السعر قيمة اي أجزاء أو مواد مطلوبه لأنجاز العمل.";
let persianNumbers = [
  /۰/g,
  /۱/g,
  /۲/g,
  /۳/g,
  /۴/g,
  /۵/g,
  /۶/g,
  /۷/g,
  /۸/g,
  /۹/g,
];
let arabicNumbers = [
  /٠/g,
  /١/g,
  /٢/g,
  /٣/g,
  /٤/g,
  /٥/g,
  /٦/g,
  /٧/g,
  /٨/g,
  /٩/g,
];
export default class OrderSummaryScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      disableOrder: false,
      userForensePoints: 0,
      lan: "en",
      points: 0,
      loading: false,
      discount: 0,
      modalVisible: false,
      jobs: [],
      phone: "",
      address: "",
      orderdate: "",
      actualSelectedDate: "",
      orderTotal: 0,
      hour: 0,
      minute: "00",
      isConsultation: false,
      limitMessage: "",
      user: null,
      isLogin: false,
      isPackage: false,
      isloaded: true,
      timeModalVisible: false,
      selectTime: false,
      currentTime: new Date(),
      time: "",
      token: "",
      package: {},
      timeInterval: [],
      referralModalVisible: false,
      otpid: "",
      referralCode: "",
      found: false,
      cartNotesAdds: "",
      cartNotesAdds_ar: "",
      promoCode: "",
      promoSuccess: false,
      alertVisible: true,
      actualTimeInterval: [
        {
          txt: "9:00AM",
          id: 0,
          ceil: 9,
        },
        {
          txt: "9:30AM",
          id: 1,
          ceil: 9.5,
        },
        {
          txt: "10:00AM",
          id: 2,
          ceil: 10,
        },
        {
          txt: "10:30AM",
          id: 3,
          ceil: 10.5,
        },
        {
          txt: "11:00AM",
          id: 4,
          ceil: 11,
        },
        {
          txt: "11:30AM",
          id: 5,
          ceil: 11.5,
        },
        {
          txt: "12:00PM",
          id: 6,
          ceil: 12,
        },

        // Was Commented Above
        {
          txt: "12:30PM",
          id: 7,
          ceil: 12.5,
        },
        {
          txt: "1:00PM",
          id: 8,
          ceil: 13,
        },
        {
          txt: "1:30PM",
          id: 9,
          ceil: 13.5,
        },
        {
          txt: "2:00PM",
          id: 10,
          ceil: 14,
        },
        {
          txt: "2:30PM",
          id: 11,
          ceil: 14.5,
        },
        {
          txt: "3:00PM",
          id: 12,
          ceil: 15,
        },
        {
          txt: "3:30PM",
          id: 13,
          ceil: 15.5,
        },
        {
          txt: "4:00PM",
          id: 14,
          ceil: 16,
        },
        {
          txt: "4:30PM",
          id: 15,
          ceil: 16.5,
        },
        {
          txt: "5:00PM",
          id: 16,
          ceil: 17,
        },
        {
          txt: "5:30PM",
          id: 17,
          ceil: 17.5,
        },
        {
          txt: "6:00PM",
          id: 18,
          ceil: 18,
        },
        {
          txt: "6:30PM",
          id: 19,
          ceil: 18.5,
        },
        {
          txt: "7:00PM",
          id: 20,
          ceil: 19,
        },
        {
          txt: "7:30PM",
          id: 21,
          ceil: 19.5,
        },
        {
          txt: "8:00PM",
          id: 22,
          ceil: 20,
        },
        {
          txt: "8:30PM",
          id: 23,
          ceil: 20.5,
        },
        {
          txt: "9:00PM",
          id: 24,
          ceil: 21,
        },
        {
          txt: "9:30PM",
          id: 25,
          ceil: 21.5,
        },
        {
          txt: "10:00PM",
          id: 26,
          ceil: 22,
        },

        // {
        //   txt: "10:30PM",
        //   id: 27,
        //   ceil: 22.5,
        // },
        // {
        //   txt: "11:00PM",
        //   id: 28,
        //   ceil: 23,
        // },
        // {
        //   txt: "11:30PM",
        //   id: 28,
        //   ceil: 23,
        // },
        // {
        //   txt: "12:00AM",
        //   id: 28,
        //   ceil: 23,
        // },
        // {
        //   txt: "12:30AM",
        //   id: 28,
        //   ceil: 23,
        // },
        // {
        //   txt: "01:00AM",
        //   id: 28,
        //   ceil: 23,
        // },
        // {
        //   txt: "01:30AM",
        //   id: 28,
        //   ceil: 23,
        // },
      ],
    };
  }
  setUserPoints = (points) => {
    this.setState({
      points: points,
      disocunt:
        parseInt(this.state.userForensePoints) >= parseInt(points) &&
        parseInt(this.state.userForensePoints) >= 500
          ? parseInt(points) * 0.01
          : 0,
    });
  };
  setModalVisible = () => {
    // this.setState({ modalVisible: true });
    this.setState({ modalVisible: true });
