    height: hp(7),
    width: wp(100),
    flexDirection: "row",
    borderTopWidth: 1,
    borderColor: "#f3f3f3",
    backgroundColor: "white"
  },
  footerItemContainerStyle: {
    height: hp(7),
    width: wp(33.33),
    justifyContent: "center",
    alignItems: "center"
  },
  tabtextStyle: {
    textAlign: "center",
    fontSize: 12
  },
  rowSubstyle: {
    flexDirection: "row",
    width: wp(95),
    paddingHorizontal: wp(5),
    justifyContent: "space-between",
    marginVertical: hp(0.5)
  }
});

