                  />
                </View>
                <View style={styles.cardinfoConatainerStyle}>
                  {/* <View style={styles.rowStyle}></View> */}
                  <View style={styles.rowStyle}>
                    <Text style={styles.titleTextStyle}>
                      {lan == "en"
